INSERT INTO `book_info` VALUES (50000018, '창백한 불꽃', '블라디미르 나보코프', '문학동네 ', '9788954655200', '《롤리타》의 대중적 성공 이후 블라디미르 나보코프가 시인, 문학교수, 번역가, 소설가로서의 모든 역량을 기울여 집필한 장편소설 『창백한 불꽃』. 1979년 국내 초역된 이래 40년 만에 새로운 번역으로 독자들과 만난다. 살해당한 시인 존 셰이드가 남긴 999행의 미완성 시 《창백한 불꽃》을 이해하기 위해 비밀스러운 주석자 찰스 킨보트의 주석에 의존할 수밖에 없게 된 독자의 위치를 이용한 게임 같은 소설이다.', '한국어', 13500.00, '2019-02-28', 9, 1, 1);
INSERT INTO `book_info` VALUES (50000020, '나의 첫 금리 공부', ' 염상훈', '원앤원북스', '9791170430308', '누구나 궁금해 하지만 아무도 쉽게 설명해주지 않는 금리에 관해 알려주는 『나의 첫 금리 공부』. 금리라는 무기를 통해 자본시장을 이해하고, 다양한 투자대상 중에서 자산을 지키고 이익을 얻는 냉철한 안목을 기를 수 있도록 도와주는 책이다. 저자는 현재와 과거의 사례를 연관시켜 금리, 경제, 물가, 신용, 환율, 그리고 현재의 금융위기와의 관계를 설명하며, 학교에서도 회사에서도 아무도 알려주지 않던, 관심도 없고 중요하게 여겨지지 않았던, 그러나 실은 매우 중요하고 흥미로운 금리를 알려준다.', '한국어', 14400.00, '2019-10-15', 6, 14, 1);
