INSERT INTO `book_info` VALUES (50000121, '생물과학', '강신성', '아카데미서적', '9788976161987', '생물과학제3판. 생물학 또는 생명과학의 시대라고 불리는 21세기에 전개될 생명과학을, 생명본질의 물질론적 해석과 생태계를 포함한 환경문제라는 두 가지 관점에서 기획, 구성하였다.', '한국어', 22500.00, '2000-02-10', 15, 886, 1);
